import React, { useState, useRef, useContext } from 'react';
import classes from './Registering.module.css';
import { useHistory } from 'react-router';
import AuthContxt from '../../store/auth-context';
import { GoogleLogin } from 'react-google-login';

const Registering = () => {
  const [isLogin, setIsLogin] = useState(false);
  const [isValid, setIsValid] = useState(true);
  //   const fullNameRef = useRef();
  //   const userNameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();
  const history = useHistory();
  const ctx = useContext(AuthContxt);

  const switchAuthModeHandler = () => {
    setIsLogin((prev) => !prev);
  };

  const responseSuccessGoogle = (res) => {
    console.log(res);
    fetch('http://localhost:3001/googlelogin', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        tokenId: res.tokenId,
        email: res.profileObj.email,
        name: res.profileObj.name,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.response === 'GoogleGood') {
          console.log(data);
          ctx.login(data.googleUser.tokenId);
          history.replace('/AllUsers');
        } else {
          ctx.login(data.tokenId);
          history.replace('/identefier');
          console.log(data);
        }
      })
      .catch((error) => console.log(error));
  };

  const responseErrorGoogle = (response) => {
    console.log(response);
  };

  const submitHandeler = (e) => {
    e.preventDefault();
    // const entredFullName = fullNameRef.current.value;
    // const entredUserName = userNameRef.current.value;
    const entredPassword = passwordRef.current.value;
    const entredEmail = emailRef.current.value;

    if (entredEmail === '' || entredPassword === '') {
      setIsValid(false);
      return;
    } else {
      setIsValid(true);
    }
    const loginUrl = 'http://localhost:3001/login';
    const signUpUrl = 'http://localhost:3001/signup';
    let url;

    if (isLogin) {
      url = loginUrl;

      fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: entredEmail,
          password: entredPassword,
        }),
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.response === 'valid') {
            ctx.login(data.user._id);
            history.replace('/AllUsers');
          } else {
            console.log('Email Or Password Maybe Wrong');
          }
        })
        .catch((error) => console.log(error));
    } else {
      url = signUpUrl;

      fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          //   fullName: entredFullName,
          //   userName: entredUserName,
          email: entredEmail,
          password: entredPassword,
        }),
      })
        .then((res) => res.json())
        .then((data) => {
          console.log(data._id);
          ctx.login(data._id);
          history.replace('/identefier');
        });
    }
  };

  return (
    <section className={classes.container}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={submitHandeler} className={classes.auth}>
        <div className={classes.control}>
          <label htmlFor='email'>Your Email</label>
          <input
            autoComplete='current-email'
            ref={emailRef}
            type='email'
            id='email'
          />
        </div>

        {/* ERROR HANDLING */}
        {!isValid && (
          <div style={{ border: '3px solid darkred', marginTop: '20px' }}>
            <p
              style={{
                color: 'darkred',
                textAlign: 'center',
                fontWeight: 'bold',
              }}
            >
              Enter A Valid Email
            </p>
          </div>
        )}

        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input
            autoComplete='current-password'
            ref={passwordRef}
            type='password'
            id='password'
          />
        </div>

        {/* ERROR HANDLING */}
        {!isValid && (
          <div style={{ border: '3px solid darkred', marginTop: '20px' }}>
            <p
              style={{
                color: 'darkred',
                textAlign: 'center',
                fontWeight: 'bold',
              }}
            >
              Enter A Valid Password
            </p>
          </div>
        )}

        <div className={classes.actions}>
          <button>{isLogin ? 'Login' : 'Create Account'}</button>

          <GoogleLogin
            clientId='773069387302-d5cih0oc4n17tn3lq9d412dm6tokh8sb.apps.googleusercontent.com'
            buttonText='Continue With Google'
            onSuccess={responseSuccessGoogle}
            onFailure={responseErrorGoogle}
            cookiePolicy={'single_host_origin'}
            SameSite='None'
          />

          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
      </form>
    </section>
  );
};

export default Registering;

