import React, { useState, useRef } from 'react';
import classes from './Registering.module.css';
import { useHistory } from 'react-router';

const Registering = () => {
  const [isLogin, setIsLogin] = useState(false);
  const [isValid, setIsValid] = useState(true);
  const emailRef = useRef();
  const passwordRef = useRef();
  const history = useHistory();

  const switchAuthModeHandler = () => {
    setIsLogin((prev) => !prev);
  };

  const submitHandeler = (e) => {
    e.preventDefault();
    const entredPassword = passwordRef.current.value;
    const entredEmail = emailRef.current.value;

    if (entredEmail === '' || entredPassword === '') {
      setIsValid(false);
      return;
    } else {
      setIsValid(true);
    }

    const singInUrl =
      'https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyCm1qzOdNAZomS_yF87W-uk_7nEiOZIaQM';
    const signUpUrl =
      'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCm1qzOdNAZomS_yF87W-uk_7nEiOZIaQM';
    let url;

    if (isLogin) {
      url = singInUrl;
    } else {
      url = signUpUrl;
    }

    fetch(url, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        email: entredEmail,
        password: entredPassword,
        returnSecureToken: true,
      }),
    }).then((res) => {
      if (res.ok) {
        return res.json().then((data) => {
          console.log(data.idToken);
          history.replace('./identefier');
        });
      } else {
        return res
          .json()
          .then((data) => {
            let errorMessage = data.error.message;
            throw new Error(errorMessage);
          })
          .catch((error) => {
            console.log(error.message);
          });
      }
    });
  };

  return (
    <section className={classes.auth}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={submitHandeler}>
        <div className={classes.control}>
          <label htmlFor='email'>Your Email</label>
          <input ref={emailRef} type='email' id='email' />
        </div>
        {!isValid && (
          <div style={{ border: '3px solid darkred', marginTop: '20px' }}>
            <p
              style={{
                color: 'darkred',
                textAlign: 'center',
                fontWeight: 'bold',
              }}
            >
              Enter A Valid Email
            </p>
          </div>
        )}

        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input ref={passwordRef} type='password' id='password' />
        </div>
        {!isValid && (
          <div style={{ border: '3px solid darkred', marginTop: '20px' }}>
            <p
              style={{
                color: 'darkred',
                textAlign: 'center',
                fontWeight: 'bold',
              }}
            >
              Enter A Valid Password
            </p>
          </div>
        )}
        <div className={classes.actions}>
          <button>{isLogin ? 'Login' : 'Create Account'}</button>
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
          <button className={classes.toggle}>Forget Your Password</button>
        </div>
      </form>
    </section>
  );
};

export default Registering;
