import React, { useState } from 'react';
import './IdintetyList.css';
import UpdateUser from './UpdateUser';

const IdintetyList = (props) => {
  const [showUpdateUser, setShowUpdateUser] = useState(false);
  const [oldData, setOldDta] = useState([]);

  const [id, setId] = useState();

  const deleteHandeler = (id) => {
    props.onDelete(id);
  };

  const updateHandeler = async (id) => {
    try {
      const response = await fetch(
        `http://localhost:3001/identefier/update/${id}`
      );
      if (!response.ok) {
        throw new Error('Somthing Went Wrong');
      }
      const data = await response.json();

      setOldDta(data);
    } catch (error) {
      console.log(error);
    }

    setId(id);
    setShowUpdateUser(true);
    // props.idintetyInfo.map((single) =>
    //   oldUser.push({
    //     name: single.id.name,
    //     age: single.id.age,
    //     hobbies: single.id.hobbies,
    //   })
    // );

    // const newName = prompt('Enter A New Name: ');

    // fetch('http://localhost:3001/update', {
    //   method: 'PUT',
    //   headers: { 'Content-Type': 'application/json' },
    //   body: JSON.stringify({
    //     // newName: newName,
    //     id: id,
    //   }),
    // });
  };
  const hideUpdateUser = () => {
    setShowUpdateUser(false);
  };
  return (
    <>
      <table class='table'>
        <thead class='thead-dark'>
          <tr>
            <th>Name</th>
            <th>Age</th>
            <th>Hobbies</th>
            <th>ID</th>
          </tr>
        </thead>
        <tbody>
          {props.idintetyInfo.map((info) => (
            <>
              <tr>
                <td>{info.name}</td>
                <td>{info.age}</td>
                <td>{info.hobbies}</td>
                <td>{info._id}</td>
                <div className='list_action'>
                  <button
                    onClick={() => deleteHandeler(info._id)}
                    className='danger'
                  >
                    DELETE
                  </button>
                  <button
                    onClick={() => updateHandeler(info._id)}
                    className='primary'
                  >
                    EDIT
                  </button>
                </div>
              </tr>
              {showUpdateUser && (
                <UpdateUser
                  id={id}
                  onCancel={hideUpdateUser}
                  oldInputsValues={oldData}
                />
              )}
            </>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default IdintetyList;
