import React, { useState, useRef, useContext } from 'react';
import classes from './Registering.module.css';
import { useHistory } from 'react-router';
import AuthContxt from '../../store/auth-context';
import axios from 'axios';

const Registering = () => {
  const [isLogin, setIsLogin] = useState(false);
  const [isValid, setIsValid] = useState(true);

  const emailRef = useRef();
  const passwordRef = useRef();
  const history = useHistory();
  const ctx = useContext(AuthContxt);

  const switchAuthModeHandler = () => {
    setIsLogin((prev) => !prev);
  };

  const submitHandeler = (e) => {
    e.preventDefault();

    const entredPassword = passwordRef.current.value;
    const entredEmail = emailRef.current.value;

    if (entredEmail === '' || entredPassword === '') {
      setIsValid(false);
      return;
    } else {
      setIsValid(true);
    }
    const loginUrl = 'http://localhost:3001/login';
    const signUpUrl = 'http://localhost:3001/signup';
    let url;

    if (isLogin) {
      url = loginUrl;

      axios
        .post(url, {
          headers: { 'Content-Type': 'application/json' },
          data: {
            email: entredEmail,
            password: entredPassword,
          },
        })
        .then((res) => {
          if (res.data.response === 'valid') {
            ctx.login(res.data.user._id);
            history.replace('/AllUsers');
          } else {
            console.log('kosomk');
          }
        })
        .catch((error) => console.log(error));
    } else {
      url = signUpUrl;

      axios
        .post(url, {
          headers: { 'Content-Type': 'application/json' },
          data: {
            email: entredEmail,
            password: entredPassword,
          },
        })
        .then((res) => {
          ctx.login(res.data._id);
          history.replace('/identefier');
        })
        .catch((error) => console.log(error));
    }
  };

  return (
    <section className={classes.container}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={submitHandeler} className={classes.auth}>
        <div className={classes.control}>
          <label htmlFor='email'>Your Email</label>
          <input ref={emailRef} type='email' id='email' />
        </div>

        {/* ERROR HANDLING */}
        {!isValid && (
          <div style={{ border: '3px solid darkred', marginTop: '20px' }}>
            <p
              style={{
                color: 'darkred',
                textAlign: 'center',
                fontWeight: 'bold',
              }}
            >
              Enter A Valid Email
            </p>
          </div>
        )}

        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input ref={passwordRef} type='password' id='password' />
        </div>

        {/* ERROR HANDLING */}
        {!isValid && (
          <div style={{ border: '3px solid darkred', marginTop: '20px' }}>
            <p
              style={{
                color: 'darkred',
                textAlign: 'center',
                fontWeight: 'bold',
              }}
            >
              Enter A Valid Password
            </p>
          </div>
        )}

        <div className={classes.actions}>
          <button>{isLogin ? 'Login' : 'Create Account'}</button>
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
      </form>
    </section>
  );
};

export default Registering;

