import React, { useRef } from 'react';
import { useHistory } from 'react-router';
import classes from '../Identety/Questions.module.css';

const UpdateUser = (props) => {
  const nameInputRef = useRef();
  const ageInputRef = useRef();
  const hobbyInputRef = useRef();
  const history = useHistory();

  const submitHandeler = async (e) => {
    e.preventDefault();

    // RESONSEBLE FOR SENDING THE UPDATING DATA TO THE BACKEND

    const newName = nameInputRef.current.value;
    const newAge = ageInputRef.current.value;
    const newHobby = hobbyInputRef.current.value;

    fetch('http://localhost:3001/update', {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        id: props.id,
        newName: newName,
        newAge: newAge,
        newHobby: newHobby,
      }),
    });

    history.replace('/AllUsers');
  };

  const cancelHandeler = () => {
    props.onCancel();
  };

  return (
    <section className={classes.box}>
      <div>
        <p>Update THE User</p>
      </div>
      <form className={classes.control} onSubmit={submitHandeler}>
        <div>
          <label>Your Name: </label>
          <input
            type='text'
            defaultValue={props.oldInputsValues.name}
            ref={nameInputRef}
          ></input>
        </div>
        <div>
          <label>Your Age: </label>
          <input
            type='number'
            defaultValue={props.oldInputsValues.age}
            ref={ageInputRef}
          ></input>
        </div>
        <div>
          <label>Your Hobbies: </label>
          <input
            type='text'
            defaultValue={props.oldInputsValues.hobbies}
            ref={hobbyInputRef}
          ></input>
        </div>
        <div className={classes.actions}>
          <button type='submit'>Submit</button>
        </div>
        <div className={classes.actions}>
          <button onClick={cancelHandeler}>Cancel</button>
        </div>
      </form>
    </section>
  );
};

export default UpdateUser;
