import React, { useState } from 'react';
import './IdintetyList.css';
import UpdateUser from './UpdateUser';

const IdintetyList = (props) => {
  const [showUpdateUser, setShowUpdateUser] = useState(false);
  const [oldUserInfo, setOldUserInfo] = useState([]);

  const [id, setId] = useState();

  const deleteHandeler = (id) => {
    props.onDelete(id);
  };

  const updateHandeler = (id) => {
    let oldUser = []

    props.idintetyInfo.map((single) => 
      oldUser.push({
        id:single._id,
        name:single.name,
        age:single.age,
        hobbies:single.hobbies
      })
    )
    setOldUserInfo(oldUser);
    setId(id);
    setShowUpdateUser(true);

    // const newName = prompt('Enter A New Name: ');

    // fetch('http://localhost:3001/update', {
    //   method: 'PUT',
    //   headers: { 'Content-Type': 'application/json' },
    //   body: JSON.stringify({
    //     // newName: newName,
    //     id: id,
    //   }),
    // });
  };

  return (
    <>
      <table className='tableCss' cellspacing='0'>
        <thead>
          <tr>
            <th>Name</th>
            <th>Age</th>
            <th>Hobbies</th>
            <th>ID</th>
          </tr>
        </thead>
        <tbody>
          {props.idintetyInfo.map((info) => (
            <tr>
              <td>{info.name}</td>
              <td>{info.age}</td>
              <td>{info.hobbies}</td>
              <td>{info._id}</td>
              <div className='list_action'>
                <button
                  onClick={() => deleteHandeler(info._id)}
                  className='danger'
                >
                  DELETE
                </button>
                <button
                  onClick={() => updateHandeler(info._id)}
                  className='primary'
                >
                  EDIT
                </button>
              </div>
              {showUpdateUser && <UpdateUser id={id} users={oldUserInfo} />}
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default IdintetyList;

