import React, { useRef } from 'react';
import OverLay from '../../layout/OverLay';
import classes from '../Identety/Questions.module.css';

const UpdateUser = (props) => {
  //   const [oldUserInfo, setOldUserInfo] = useState();
  const nameInputRef = useRef();
  const ageInputRef = useRef();
  const hobbyInputRef = useRef();

  //   const url = `http://localhost:3001/identefier/${props.id}`;

  const submitHandeler = (e) => {
    e.preventDefault();
    console.log(props.id);

    // IT MUST RESEVE THE ID FROM IDINTETY LIST COMPONENT
    // RESONSEBLE FOR SENDING THE UPDATING DATA TO THE BACKEND && REPRESENT THE OLD DATA WITH VALUES IN THE INPUTS

    const newName = nameInputRef.current.value;
    const newAge = ageInputRef.current.value;
    const newHobby = hobbyInputRef.current.value;

    fetch('http://localhost:3001/update', {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        id: props.id,
        newName: newName,
        newAge: newAge,
        newHobby: newHobby,
      }),
    });
  };

  const cancelHandeler = () => {
    props.onCancel();
  };
  return (
    <OverLay>
      <section className={classes.box}>
        <div>
          <p>Update THE User</p>
        </div>
        <form className={classes.control} onSubmit={submitHandeler}>
          <div>
            <label>Your Name: </label>
            <input type='text' ref={nameInputRef}></input>
          </div>
          <div>
            <label>Your Age: </label>
            <input type='number' ref={ageInputRef}></input>
          </div>
          <div>
            <label>Your Hobbies: </label>
            <input type='text' ref={hobbyInputRef}></input>
          </div>
          <div className={classes.actions}>
            <button type='submit'>Submit</button>
          </div>
          <div className={classes.actions}>
            <button onClick={cancelHandeler}>Cancel</button>
          </div>
        </form>
      </section>
    </OverLay>
  );
};

export default UpdateUser;
