import React, { useState } from 'react';
import { FaRegEdit } from 'react-icons/fa';
import { FaTimes } from 'react-icons/fa';
import './IdintetyList.css';
import UpdateUser from './UpdateUser';

const IdintetyList = (props) => {
  const [showUpdateUser, setShowUpdateUser] = useState(false);
  const [oldData, setOldDta] = useState([]);

  const [id, setId] = useState();

  const deleteHandeler = (id) => {
    props.onDelete(id);
  };

  const updateHandeler = async (id) => {
    try {
      const res = await fetch(`http://localhost:3001/identefier/update/${id}`);
      if (!res.ok) {
        throw new Error('Somthing Went Wrong While Getting Data');
      }
      const data = await res.json();
      setOldDta(data);
    } catch (error) {
      console.log(error);
    }

    // try {
    //   const response = await fetch(
    //     `http://localhost:3001/identefier/update/${id}`
    //   );
    //   if (!response.ok) {
    //     throw new Error('Somthing Went Wrong');
    //   }
    //   const data = await response.json();

    //   setOldDta(data);
    // } catch (error) {
    //   console.log(error);
    // }

    setId(id);
    setShowUpdateUser(true);
    // props.idintetyInfo.map((single) =>
    //   oldUser.push({
    //     name: single.id.name,
    //     age: single.id.age,
    //     hobbies: single.id.hobbies,
    //   })
    // );

    // const newName = prompt('Enter A New Name: ');

    // fetch('http://localhost:3001/update', {
    //   method: 'PUT',
    //   headers: { 'Content-Type': 'application/json' },
    //   body: JSON.stringify({
    //     // newName: newName,
    //     id: id,
    //   }),
    // });
  };
  const hideUpdateUser = () => {
    setShowUpdateUser(false);
  };
  return (
    <>
      <div className='container'>
        <table className='table'>
          <thead>
            <tr>
              <th>Name</th>
              <th>Age</th>
              <th>Hobbies</th>
              <th>ID</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {props.idintetyInfo.map((info) => (
              <tr key={info._id}>
                <td>{info.name}</td>
                <td>{info.age}</td>
                <td>{info.hobbies}</td>
                <td>{info._id}</td>
                <td className='list_action'>
                  <FaRegEdit
                    className='primary'
                    onClick={() => updateHandeler(info._id)}
                  />
                  <FaTimes
                    className='danger'
                    onClick={() => deleteHandeler(info._id)}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {showUpdateUser && (
        <UpdateUser
          id={id}
          onCancel={hideUpdateUser}
          oldInputsValues={oldData}
        />
      )}
    </>
  );
};

export default IdintetyList;
