import React, { useState, useRef, useContext } from 'react';
import classes from './Registering.module.css';
import { useHistory } from 'react-router';
import AuthContxt from '../../store/auth-context';

const Registering = () => {
  const [isLogin, setIsLogin] = useState(false);
  const [isValid, setIsValid] = useState(true);
  const fullNameRef = useRef();
  const userNameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();
  const history = useHistory();
  const ctx = useContext(AuthContxt);

  const switchAuthModeHandler = () => {
    setIsLogin((prev) => !prev);
  };

  const submitHandeler = (e) => {
    e.preventDefault();
    const entredFullName = fullNameRef.current.value;
    const entredUserName = userNameRef.current.value;
    const entredPassword = passwordRef.current.value;
    const entredEmail = emailRef.current.value;

    if (entredEmail === '' || entredPassword === '') {
      setIsValid(false);
      return;
    } else {
      setIsValid(true);
    }

    const url = 'http://localhost:3001/app/signUp';

    fetch(url, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        fullName: entredFullName,
        userName: entredUserName,
        email: entredEmail,
        password: entredPassword,
      }),
    }).then((res) => {
      if (res.ok) {
        return res.json().then((data) => {
          ctx.login(data.idToken);

          history.replace('/identefier');
        });
      } else {
        return res
          .json()
          .then((data) => {
            let errorMessage = data.error.message;
            throw new Error(errorMessage);
          })
          .catch((error) => {
            alert(error.message);
          });
      }
    });
  };

  return (
    <section className={classes.auth}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={submitHandeler}>
        <div className={classes.control}>
          <label>Your fullName</label>
          <input ref={fullNameRef} type='text' />
        </div>
        {!isValid && (
          <div style={{ border: '3px solid darkred', marginTop: '20px' }}>
            <p
              style={{
                color: 'darkred',
                textAlign: 'center',
                fontWeight: 'bold',
              }}
            >
              Enter A Valid fullName
            </p>
          </div>
        )}
        <div className={classes.control}>
          <label>Your userName</label>
          <input ref={userNameRef} />
        </div>
        {!isValid && (
          <div style={{ border: '3px solid darkred', marginTop: '20px' }}>
            <p
              style={{
                color: 'darkred',
                textAlign: 'center',
                fontWeight: 'bold',
              }}
            >
              Enter A Valid userName
            </p>
          </div>
        )}
        <div className={classes.control}>
          <label htmlFor='email'>Your Email</label>
          <input ref={emailRef} type='email' id='email' />
        </div>
        {!isValid && (
          <div style={{ border: '3px solid darkred', marginTop: '20px' }}>
            <p
              style={{
                color: 'darkred',
                textAlign: 'center',
                fontWeight: 'bold',
              }}
            >
              Enter A Valid Email
            </p>
          </div>
        )}

        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input ref={passwordRef} type='password' id='password' />
        </div>
        {!isValid && (
          <div style={{ border: '3px solid darkred', marginTop: '20px' }}>
            <p
              style={{
                color: 'darkred',
                textAlign: 'center',
                fontWeight: 'bold',
              }}
            >
              Enter A Valid Password
            </p>
          </div>
        )}
        <div className={classes.actions}>
          <button>{isLogin ? 'Login' : 'Create Account'}</button>
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
          <button className={classes.toggle}>Forget Your Password</button>
        </div>
      </form>
    </section>
  );
};

export default Registering;
