import React, { useState, useEffect } from 'react';

import IdintetyList from './IdintetyList';
import classes from './GetAllUsers.module.css';
import LoadingSppiner from '../../layout/LoadingSppiner';

const MyProfile = () => {
  const [idintety, setIdintety] = useState([]);
  const [loading, setLoading] = useState(false);
  const url = 'http://localhost:3001/identefier';

  // LOADING ALL USERS TO THE SCREEN WITH useEffect()

  /*  
      try{  
        const res = fetch(url);
        if(!res.ok){
          throw new Error(' Failed To Get The Users Idintety ')
        }
        cosnt data = res.json();
        setIdintety(data);

      }catch(err){
        console.log(err)
      }
  */

  useEffect(() => {
    setLoading(true);

    fetch(url)
      .then((res) => {
        if (!res.ok) {
          throw Error('Failed To Get The Users Idintety');
        }
        return res.json();
      })
      .then((data) => {
        setIdintety(data);
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, [url]);

  // HANDLING THE DELET USER INFO METHOD BY CLICKING

  const deleteHandeler = (id) => {
    fetch(`http://localhost:3001/remove/info/${id}`, {
      method: 'DELETE',
    }).then((res) => res.json());

    // prompt('Are You Sure You Want To Delete This Account');
    // fetch(`http://localhost:3001/delete/email/${id}`, {
    //   method: 'DELETE',
    // }).then((res) => res.json());
  };
  return (
    <>
      <div className={classes.actions}>
        <h1>All Users</h1>
      </div>
      {loading ? (
        <LoadingSppiner />
      ) : (
        <IdintetyList idintetyInfo={idintety} onDelete={deleteHandeler} />
      )}
    </>
  );
};

export default MyProfile;
